INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC39FE2809EC38BC3BB1BC38511C3A4EF, 0xE284A244C6923F18C39711C3A4E284A2, '95', 'Microsoft', '95', 3, '1995-07-18 01:00:00', null, 'True', '', 8000000, '386DX', 28000000, 'No', '', 'GUI', 'False', '', 'Microsoft Office for Windows 95 (AKA Microsoft Office 7) was a fully 32 bit compliant version of Office released along-side Windows 95. It is also natively compatible with Windows NT 3.51. Few new features were introduced in Word which was largely reminiscent of version 6.0/Office 4.0.  Word''s version is 7 in this release and the other Office members have had their version numbers raised to match that of Word.', 'Windows', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC3A37D1AC3901BC38511C3A4E284A2EF, 0xE284A244C6923F18C39711C3A4E284A2, '97/98', 'Microsoft', '97-98', 4, '1996-11-17 07:00:00', null, 'True', '', 8000000, '486', 60000000, 'No', '', 'GUI', 'False', '', 'The Office 97 release introduced Outlook into the suite and massively expanded upon the feature set of applications, adding support for the new Internet world. It also included animated Office Assistant characters based on the guides from [Microsoft BOB](/product/microsoft-bob). Office 97 was the last to be offered on floppy disk. (Wanted: there should also be 1.44mb versions in both Standard and Pro, as well as perhaps SR-2)

For Mac, Office 98 was released, adding features from 97 and more Mac specific features. It was praised as an improvement over the 4.2 release on that platform, which was considered a sloppy Windows port and poorly received.', 'Windows,MacOS', null);
