INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC2A06F39791CC38311C3A4E284A2C3A5, 0xE2809A2C30C2A91A3211C3A4E284A2EF, '7.0', 'Apple', '70', 1, '1991-05-12 05:00:00', null, 'True', '', 0, '68000', 0, 'No', '', 'GUI', 'False', '', 'Mac System 7 adds better color support, a help system, cleaned up System Folder, support for the new 68040 Quadras, and rewriting many components from Pascal and Assembler to C. It is, however, a heavier release, lacking support for the 128k and 512k, and leaving some compacts struggling. System 6 may be a more fitting choice for some vintage systems.', 'MacOS', null);
