INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x106EC2B0C3891CC38411C3A4E284A2EF, 0x09C397C2A0C3AF1CC38111C3A4E284A2, '1', 'Adobe', '1', 1, '1994-10-13 01:00:00', null, 'True', '', 0, '386', 0, 'No', null, 'GUI', 'False', 'Originally the Reader software was not free. During Install it asks for a serial number. ', 'Acrobat Reader 1.0 for DOS is a 32-bit DOS4GW extended MS-DOS application. It has a built-in GUI that mimics the Windows 3.1 user interface. It supports VGA and VESA graphics modes up to 1024x768x256. This is the only version released for DOS.

