INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xE280A1C2A8C2A9C2A61CC38211C3A4EF, 0xC3B971C3A4C3811BC3BF11C3A4E284A2, 'Server 1.0', 'Apple', 'server-10', 2, '1999-03-15 07:00:00', null, 'True', '', 64000000, 'PowerPC G3', 2000000000, 'No', '', 'GUI', 'False', 'Installation notes: This version of Mac OS X is for <b>PowerPC</b>.  x86 emulators like VirtualBox, VMWare or Virtual PC will not work.  These releases will only run on specific models of PPC Apple Macintosh computers. QEMU (PPC) is known to run Mac OS X Server 1.0. ', 'Mac OS X Server 1.0 was a preview of what was in store for Mac OS X.  It was based on the earlier Rhapsody releases and contained a mix of technologies.  The UI style was that of the Mac OS 8 Platinum style, file management took place through the NeXTStep Workspace Manager, and classic Mac OS applications could be ran in the Mac OS 8 "blue box" environment.  The display server is still the Display PostScript from NeXTStep, and not the PDF based Quartz that would be introduced with the Aqua UI.', 'MacOS', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x1CC385C2A8421DC3AB11C3A4E284A2EF, 0x650D49C5921A2E11C3A4E284A2C3A570, 'OSR 1', '', 'osr-1', 5, '1995-12-29 02:00:00', '2001-12-29 02:00:00', 'False', '', 4000000, '386', 50000000, 'No', 'TCP/IP, NetBIOS, LAN Manager', 'GUI', 'False', '', 'Windows 95 OSR 1, ("A" version) was a minor update of various core files. It was available as a downloadable update and as a full OEM CD. ', 'Windows', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC2B371C2AFC3871DC3AB11C3A4E284A2, 0xC2B9C3B925C39415C2A911C3A4E284A2, '1.01', '', '101', 5, '1985-11-14 08:00:00', '2001-12-30 08:00:00', 'False', '', 320000, '8088', 720000, 'No', null, 'GUI', 'False', 'Installation notes:
- The installer expects you to run it from the A: drive. Either write the images to disks or SUBST from a hard drive folder. 
- Windows 1.x will run on DOS 5 and later as long as SETVER is loaded and there is a SETVER entry for "WIN100.BIN" to report "3.34". Otherwise it will crash at startup. 
- COMMAND.COM will only run in a window in DOS 3.3 and earlier. 
- 1.x does not support PS/2 mice. However if you overwrite MOUSE.DRV with the Windows 2.03 version, and install Windows with the serial mouse option, then they will work. 

For the VAXMate DOS+Windows 1.x bundles see: [MS-DOS 3.10](/product/ms-dos/310).
For Tandy 2000 support, see [Tandy 2000 MS Windows Pre-Install](/product/tandy-2000-ms-windows-pre-Installation).

Winworld has the following Windows 1.x applications: [Balance of Power](/product/balance-of-power/1985-edition),  [Aldus PageMaker 1.0 and 1.0a](/product/aldus-pagemaker/100), [Micrografx In-A-Vision](/product/in-a-vision/123), [ZSoft PC Paintbrush 1.05 for Windows](/product/pc-paintbrush/105), [Micrografx Windows Draw 1.04](/product/micrografx-draw/104), [Micrografx Portfolio](/product/micrografx-portfolio),  [Opus/WinSong Composer](/product/winsong-composer), [Omnis Quartz](/product/omnis/quartz), [Micrografx Windows Graph](/product/micrografx-windows-graph). There is also an [800*600 SVGA driver](http://www.seasip.info/DOS/Win1/vga.html). Additional shareware and drivers can be found on [Toastytech''s Applications for Windows 1.x and 2.x](http://toastytech.com/guis/win1x2x.html).', 'Windows 1.01 was the first release made available on store shelves. It was also bundled as a graphical run-time with several applications. ', 'Windows', null);
