INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC3993FC5B8C380246B11C3A4E284A2EF, 0xC2BAC3B33DE2809C246B11C3A4E284A2, 'NT 3.1 Pre-Release', 'Microsoft', 'nt-os-2', 1, '1991-01-01 02:00:00', null, 'True', '', 0, '386', 0, 'No', null, 'GUI', 'False', '', 'These builds are early pre-releases, that lead up to the final NT 3.1. They are ocasionally referred to as "NT OS/2", as the core technology was developed by Microsoft for use in future IBM OS/2 products. However, after the IBM-MS split, it became "Windows", and is branded as such. 

