INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC2A1E280BAE280A2C382261511C3A4EF, 0xC2A7C3B973701BC38011C3A4E284A2EF, '5.x (DOS and OS/2)', 'Microsoft', '5x-dos', 7, '1989-04-24 05:00:00', null, 'True', '', 512000, '8088', 0, 'No', null, 'Text', 'False', '', 'Word 5 for DOS defaults to text video mode operation. To use graphics mode, you must setup Word for your graphics card and then specify the desired video mode. 

