INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC2B8C39228C3A0261511C3A4E284A2EF, 0xC2A7C3B973701BC38011C3A4E284A2EF, '5.x (UNIX)', 'Microsoft', '5x-unix', 8, '1990-12-31 02:00:00', null, 'True', '', 0, '', 0, 'No', null, 'Text', 'False', '', 'The origins of Microsoft Word date back to "Multi-Tool Word" developed for Xenix. Later in its life, the character-based Microsoft Word was still available for SCO Unix/Xenix. There was also a port to the AT&T Unix PC. 

