INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC3A2C2B3C2A2C692261511C3A4E284A2, 0xC2A7C3B973701BC38011C3A4E284A2EF, '6.x', 'Microsoft', '6x', 9, '1993-05-17 01:00:00', null, 'True', '', 0, '8088(DOS) 286(Win) 68020 or PPC(Mac)', 0, 'No', null, 'GUI', 'False', '', 'To bring version numbers in line across platforms, Microsoft bumped Word for Windows from version 2 to 6.
