INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x53C394C2ABC2AB265111C3A4E284A2EF, 0xC3AA75C3BEC3971BC3BF11C3A4E284A2, '3.x', 'NeXT', '3x', 2, '1992-09-06 05:00:00', null, 'True', '', 0, '68030, 486, Sparc, HPPA', 0, 'No', null, 'GUI', 'False', 'Note: The 3.x CDs are in BSD Unix format rather than standard ISO. Many CD utilities will not directly open these images, requiring you to write them as "raw" data. ', 'NeXTSTEP 3.x saw many major changes. 3.0 is only for NeXT 68k hardware, but 3.1, 3.2, and 3.3 also support Intel 32-bit x86 hardware. NeXTSTEP 3.3 was the final release under the NeXTSTEP name, and the most popular on NeXT hardware. ', 'Unix', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x671DC388C3B8265111C3A4E284A2C3A5, 0xC3AA75C3BEC3971BC3BF11C3A4E284A2, '4.x', '', '4x', 3, '1996-11-30 17:00:00', null, 'False', '', 0, 'm68k, i486, or SPARC', 0, 'No', null, 'GUI', 'False', '', '4.0 was the first release under the name "OpenStep". The name change reflects a shift from being just an OS to a portable API set.', 'Unix', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x70C2B5C384C2AE265211C3A4E284A2EF, 0xC3B05477461B1711C3A4E284A2C3A570, '4.0x', 'Microsoft', '4x', 8, '1988-10-06 01:00:00', null, 'True', '', 0, '8088', 0, 'No', null, 'Text', 'False', '', 'MS-DOS 4 added support for hard drive partitions up to 2GB. It used much more of the base 640K, and was somewhat buggy. 

Microsoft had long planned that MS-DOS "4" would be a multitasking-capable operating system, but IBM had insisted on creating a new version of regular DOS for use with their PS/2 computers. The below versions, as released to the consumer market, are based on DOS 3.3x and IBM''s PC-DOS 4.0 enhancements. Although essentially scrapped, some narrow European markets did receive Microsoft''s [Multitasking MS-DOS 4.0](/product/multitasking-ms-dos-)

Microsoft''s initial 4.00 release (File dates 10/6/1988) was quickly followed up by 4.01, making this an uncommon version. To add to the confusion, most OEMS badged their 4.01 disks as "4" or "4.0". 
', 'DOS', null);
INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0x6EC39DC3A9C2B1265311C3A4E284A2EF, 0x66C5B8C38871265311C3A4E284A2C3A5, '1.x', '', '1x', 1, '1989-12-31 02:00:00', null, 'True', '', 0, 'Sun Sparc', 0, 'No', null, 'GUI', 'False', 'Note that installing on real hardware may require a SCSI CD drive that supports 512 byte sectors. ', 'Although Solaris 2 represented a shift in the core architecture, Sun retroactively brought some versions of SunOS under the Solaris 1 name for marketing purposes. 
The following versions of SunOS and Solaris directly correspond:

SunOS 4.1.1.x <-> Solaris 1.0 	

SunOS 4.1.2 <-> Solaris 1.0.1

SunOS 4.1.3 <-> Solaris 1.1

SunOS 4.1.3C <-> Solaris 1.1C

SunOS 4.1.3_U1 <-> Solaris 1.1.1

SunOS 4.1.3_U1B <-> Solaris 1.1.1B

SunOS 4.1.4 <-> Solaris 1.1.2', 'Unix', null);
