INSERT INTO winworld.Releases (ReleaseUUID, ProductUUID, Name, VendorName, Slug, ReleaseOrder, ReleaseDate, EndOfLife, FuzzyDate, Arch, RAMRequirement, CPURequirement, DiskSpaceRequired, MultiUser, Networking, Type, SerialRequired, InstallInstructions, Notes, Platform, DefaultDownload) VALUES (0xC2BDC3A114C39A265311C3A4E284A2EF, 0x66C5B8C38871265311C3A4E284A2C3A5, '7.x', '', '7x', 3, '1998-11-10 12:00:00', '2008-07-31 12:00:00', '', '', 0, 'Sun SPARC CPU/ Intel Pentium 3 or higher', 0, 'No', null, 'GUI', 'False', '', 'This version of Solaris was the first 64 bit UltraSPARC release. This release is technically 2.7, however Sun changed the version naming scheme starting with this version, calling this version 7.', 'Unix', null);
